using System;
using System.Data;
using System.Collections;
using EleSy.Common.Script;
using System.Diagnostics;
using System.IO;




namespace Scripts
{
     /// <summary>
     /// Класс используется для подготовки набора данных.
     /// </summary>
     public class Script : IDataScript
     {
          /// <summary>
          /// Возвращает набор данных.
          /// </summary>
          /// <param name="parameters">Параметры скрипта</param>
          /// <returns>Набор данных</returns>
          public DataSet GetData(object[] parameters )
          { 
string MasterIP = "localhost";
string SlaveIP = "localhost";
string OPCServer = "EleSy.HDADualSource";
string OPCServer1 = "Elesy.DualSource";



//DateTime endDate = DateTime.Now;//Конец интервала выборки

DateTime startDate = System.Convert.ToDateTime(parameters[0]);//Конец интервала выборки

DateTime endDate = startDate.AddDays(1) ;//Начало интервала выборки
if (endDate > DateTime.Now) {endDate =DateTime.Now;}

//startDate =startDate.AddHours(-startDate.Hour) ;
//startDate =startDate.AddMinutes(-startDate.Minute) ;
//startDate =startDate.AddSeconds(-startDate.Second) ;
//endDate = startDate.AddDays(1);

DateTime curDate = startDate;//Переменная для запоминания текущей метки времени выборки

//Создаем массив для записи
ArrayList values = new ArrayList();
Opc.URL url = new Opc.URL("opchda://" + MasterIP + "/" + OPCServer);
Opc.Hda.Server server = new Opc.Hda.Server(new OpcCom.Factory(), url);

Opc.URL url1 = new Opc.URL("opcda://" + MasterIP + "/" + OPCServer1);
Opc.Da.Server server1 = new Opc.Da.Server(new OpcCom.Factory(), url1);

			DataSet result = new DataSet();
			DataTable table = new DataTable("ScriptData");

			//Объявление таблицы для хранения данных

table.Columns.Add("Tag", typeof(System.String));
table.Columns.Add("Value", typeof(System.String));
table.Columns.Add("Date", typeof(System.String));
table.Columns.Add("Time", typeof(System.String));
table.Columns.Add("Quality", typeof(System.String));
table.Columns.Add("desc", typeof(System.String));
table.Columns.Add("obj", typeof(System.String));

result.Tables.Add(table);

			//Объявляем строковый массив с тегами

StreamReader streamReader = System.IO.File.OpenText(@"C:\Data\Reports\TomskAzot.ini");
//Вспомогательная переменная - строка
string tmp = "";


string prefix = "";

//Массив, в который нужно записать значения
ArrayList TEMP = new ArrayList();
ArrayList TEMP_desc = new ArrayList();
ArrayList TEMP_type = new ArrayList();
ArrayList TEMP_eunit = new ArrayList();
//Пишем, пока в файле есть строки

while (!streamReader.EndOfStream)
{
    //tmp = prefix + streamReader.ReadLine();
    tmp = prefix + streamReader.ReadLine();
    //Помещаем в массив
    TEMP.Add(tmp);
}



//Соединяемся с сервером
try
{
    server.Connect();
}
catch
{
url = new Opc.URL("opchda://" + SlaveIP + "/" + OPCServer);
server = new Opc.Hda.Server(new OpcCom.Factory(), url);
try
{
    server.Connect();
}
catch
{ 
}
}


try
{
    server1.Connect();
}
catch
{
url1 = new Opc.URL("opcda://" + SlaveIP + "/" + OPCServer1);
server1 = new Opc.Da.Server(new OpcCom.Factory(), url1);
try
{
    server1.Connect();
}
catch
{ 
}
}


System.Threading.Thread.Sleep (1500); // время необходимое для подключения к \\источнику данных; ОБЯЗАТЕЛЬНЫЙ ПАРАМЕТР! Параметр в скобках задается в \\миллисекундах;

Opc.Da.Item[] TMP_desc = new Opc.Da.Item[TEMP.Count];
Opc.Da.Item[] TMP_type = new Opc.Da.Item[TEMP.Count];
Opc.Da.Item[] TMP_eunit = new Opc.Da.Item[TEMP.Count];
Opc.ItemIdentifier[]  TMP1 = new Opc.ItemIdentifier[TEMP.Count];

for (int i = 0; i < TEMP.Count; i++)
{   
    string tag;
    tag=TEMP[i].ToString();
    TMP1[i] =new Opc.ItemIdentifier(tag ); 

    if (tag.IndexOf("status") != -1)
   	{TMP_desc[i] = new Opc.Da.Item(new Opc.ItemIdentifier(tag.Replace(".state.status",".Description")));
	 TMP_type[i] = new Opc.Da.Item(new Opc.ItemIdentifier(tag.Replace(".state.status",".popup")));}
    else
	{TMP_desc[i] = new Opc.Da.Item(new Opc.ItemIdentifier(tag + ".Description"));
	TMP_type[i] = new Opc.Da.Item(new Opc.ItemIdentifier(tag + ".popup"));  }

    TMP_eunit[i] = new Opc.Da.Item(new Opc.ItemIdentifier(tag + ".eunit")); 

}
//Читаем данные
Opc.IdentifiedResult[] items1 = server.CreateItems(TMP1);
Opc.Da.ItemValueResult[] vTMP_desc = server1.Read(TMP_desc);
Opc.Da.ItemValueResult[] vTMP_type = server1.Read(TMP_type);
Opc.Da.ItemValueResult[] vTMP_eunit = server1.Read(TMP_eunit);

for (int i = 0; i < TEMP.Count; i++)
{   
    TEMP_desc.Add(System.Convert.ToString(vTMP_desc[i].Value)); 
    TEMP_type.Add(System.Convert.ToString(vTMP_type[i].Value));
    TEMP_eunit.Add(System.Convert.ToString(vTMP_eunit[i].Value));  
}
try
{
    server1.Disconnect();
}
catch
{
}



Opc.Hda.Time startTime = new Opc.Hda.Time(startDate);
Opc.Hda.Time endTime = new Opc.Hda.Time(endDate);


while(curDate <= endDate)
{
Opc.Hda.Time curTime = new Opc.Hda.Time(curDate);

Opc.Hda.ItemValueCollection[] values1 = server.ReadRaw(curTime ,  curTime , 100, true, items1);


for (int i = 0; i < values1.Length; i++)
{
        DataRow row = table.NewRow();
        table.Rows.Add(row);

        row[0] = values1[i].ItemName;
	row[1] = values1[i][0] == null ? System.Convert.ToDouble(DBNull.Value) : (System.Math.Round(System.Convert.ToDouble(values1[i][0].Value), 2)); 



	//row[2] = values1[i][0].Timestamp.ToString("yyyy.MM.dd HH:mm");
	row[2] = curDate.ToString("yyyy.MM.dd");
        row[3] = curDate.ToString("HH:mm");
	if((curDate == endDate) && (curDate.ToString("HH:mm") == "00:00")){row[3]="24:00";}

        row[4] = values1[i][0].Quality.GetCode(); 
  	row[5] =  TEMP_desc[i]; 
	row[6] =  TEMP_type[i];  

	if ((System.Convert.ToString(row[6])=="AP") || (System.Convert.ToString(row[6])=="AP1H"))  {row[5]=row[5]+", " + TEMP_eunit[i]; }


	if (System.Convert.ToString(row[6])=="AGT")
        {switch (System.Convert.ToString(row[1]))
         {
         case "1":
              row[1] ="Отключен, нет напряжения";
	      break; 
          case "2":
              row[1] ="Отключен, цепи вкл. неиспр.";
	      break; 
          case "3":
              row[1] ="Отключен";
	      break; 
          case "4":
              row[1] ="Отключается";
	      break; 
          case "5":
              row[1] ="Включается";
	      break; 
          case "6":
              row[1] ="Включен, пускатель неиспр.";
	      break; 
          case "7":
              row[1] ="Включен, цепи откл. неиспр.";
	      break; 
          case "8":
              row[1] ="Включен";
	      break; 
          case "0":
              row[1] ="Н/Д";
	      break; 
        }
      }


	if ((System.Convert.ToString(row[6])=="VLV")|| (System.Convert.ToString(row[6])=="REG"))
        {switch (System.Convert.ToString(row[1]))
         {
          case "1":
              row[1] ="Открыт";
	      break; 
          case "2":
              row[1] ="Закрыт";
	      break; 
          case "3":
              row[1] ="Промежуток";
	      break; 
          case "4":
              row[1] ="Неопред.";
	      break; 
          case "5":
              row[1] ="В движении";
	      break; 
          case "6":
              row[1] ="Открывается";
	      break; 
          case "7":
              row[1] ="Закрывается";
	      break; 
          case "0":
              row[1] ="Н/Д";
	      break; 
        }
      }
}
curDate=curDate.AddHours(2);
}




try
{
    server.Disconnect();
}
catch
{
}


			return result;		
          }
     }

class Datas
{
	string Name {get; set;}

}
}

