          public DataSet GetData(object[] parameters )
          { 
string MasterIP = "localhost";
string SlaveIP = "10.41.17.73";

string OPCServer1 = "Elesy.DualSource";




//Создаем массив для записи
ArrayList values = new ArrayList();


Opc.URL url1 = new Opc.URL("opcda://" + MasterIP + "/" + OPCServer1);
Opc.Da.Server server1 = new Opc.Da.Server(new OpcCom.Factory(), url1);

			DataSet result = new DataSet();
			DataTable table = new DataTable("ScriptData");

			//Объявление таблицы для хранения данных

table.Columns.Add("Tag", typeof(System.String));
table.Columns.Add("Value", typeof(System.String));
table.Columns.Add("Quality", typeof(System.String));
table.Columns.Add("desc", typeof(System.String));
table.Columns.Add("obj", typeof(System.String));

result.Tables.Add(table);

			//Объявляем строковый массив с тегами

StreamReader streamReader = System.IO.File.OpenText(@"C:\Data\Reports\TomskAzot.ini");
//Вспомогательная переменная - строка
string tmp = "";


string prefix = "";

//Массив, в который нужно записать значения
