Opc.Da.Item[] TMP_desc = new Opc.Da.Item[TEMP.Count];
Opc.Da.Item[] TMP_type = new Opc.Da.Item[TEMP.Count];
Opc.Da.Item[] TMP_eunit = new Opc.Da.Item[TEMP.Count];
Opc.Da.Item[] TMP = new Opc.Da.Item[TEMP.Count];

for (int i = 0; i < TEMP.Count; i++)
{   
    string tag;
    tag=TEMP[i].ToString();
    TMP[i] = new Opc.Da.Item(new Opc.ItemIdentifier(tag)); 
    if (tag.IndexOf("status") != -1)
   	{TMP_desc[i] = new Opc.Da.Item(new Opc.ItemIdentifier(tag.Replace(".state.status",".Description")));
	TMP_type[i] = new Opc.Da.Item(new Opc.ItemIdentifier(tag.Replace(".state.status",".popup")));
	}
    else
	{TMP_desc[i] = new Opc.Da.Item(new Opc.ItemIdentifier(tag + ".Description"));
	 TMP_type[i] = new Opc.Da.Item(new Opc.ItemIdentifier(tag + ".popup")); }

    TMP_eunit[i] = new Opc.Da.Item(new Opc.ItemIdentifier(tag + ".eunit")); 

