Opc.Da.ItemValueResult[] vTMP_type = server1.Read(TMP_type);
Opc.Da.ItemValueResult[] vTMP_eunit = server1.Read(TMP_eunit);

for (int i = 0; i < TEMP.Count; i++)
{   

	DataRow row = table.NewRow();
	table.Rows.Add(row);

	tmp=TEMP[i].ToString();	// имя тэга
	row[0]=tmp;		// имя тэга
	row[1] = System.Convert.ToString(System.Math.Round(System.Convert.ToDouble(TMP1[i].Value),2));
	row[2] = TMP1[i].Quality.GetCode();
	row[3] = vTMP_desc[i].Value;
	row[4] = vTMP_type[i].Value;


	if ((System.Convert.ToString(row[4])=="AP") || (System.Convert.ToString(row[4])=="AP1H")){row[3]=row[3]+", " + vTMP_eunit[i].Value; }


	if (System.Convert.ToString(row[4])=="AGT")
        {switch (System.Convert.ToString(row[1]))
         {
          case "1":
              row[1] ="Отключен, нет напряжения";
	      break; 
          case "2":
              row[1] ="Отключен, цепи вкл. неиспр.";
	      break; 
          case "3":
              row[1] ="Отключен";
	      break; 
          case "4":
              row[1] ="Отключается";
